//View any profile
//Can update info
//Cannot be same certificate for different users
//Simulation if block is corrupted
//None can modify
//Ban list for malicious nodes
//Connect with Rinkeby network (truffle migrate --reset --network=rinkeby)_____Done

import React, { Component } from 'react';
import Web3 from 'web3';
import './App.css';
import Authenticate from '../abis/Authenticate.json';

const ipfsClient = require('ipfs-http-client')
const ipfs = ipfsClient({ host: 'ipfs.infura.io', port: 5001, protocol: 'https' }) // leaving out the arguments will default to these values

class App extends Component {

  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
    /*
    const accounts = await window.web3.eth.getAccounts()
    console.log(accounts)
    const contract = await window.web3.eth.Contract(Authenticate.abi, Authenticate.networks[5777].address)
    console.log(contract)
    const ca = await contract.methods.CAaddress().call()
    console.log(ca)
    console.log(await window.web3.eth.Contract(Authenticate.abi, Authenticate.networks[5777].address).methods.CAaddress(accounts[0]).call())
    */
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  async loadBlockchainData() {
    const web3 = window.web3
    // Load account
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    this.setState({ useraccount: accounts[0] })
    const networkId = await web3.eth.net.getId()
    const networkData = Authenticate.networks[networkId]

    //if(await this.state.contract.methods.UserInfo(this.state.useraccount).call()){
    //console.log(await this.state.contract.methods.UserInfo(this.state.useraccount).call())
    //}
    //if(this.state.useraccount){
    //  const memeHash = await this.state.contract.methods.UserInfo(this.state.useraccount).call()
    //console.log(memeHash)}
    


    if(networkData) {
      const contract = web3.eth.Contract(Authenticate.abi, networkData.address)
      this.setState({ contract })
      //const memeHash = await contract.methods.get().call()
      //this.setState({ memeHash })
    } else {
      window.alert('Smart contract not deployed to detected network.')
    }
  }
  async show()  {

    //this.setState({ roll: roll })

    // console.log(this.state)
    // const ua = this.state.useraccount
    // const acc = this.state.account
    
    // console.log(ua,acc)
    // await this.state.contract.methods.SetCurrentUser(ua).send({ from: acc })
    // console.log(await this.state.contract.methods.UserInfo().call())
  }

  constructor(props) {
    super(props)

    this.state = {
      memeHash: '',
      contract: null,
      web3: null,
      buffer: null,
      account: null,
      useraccount: null,
      name: null,
      hash: null,
      key : null,
      roll : null
    }
  }
  

  captureFile = (event) => {
    event.preventDefault()
    const file = event.target.files[0]
    const reader = new window.FileReader()
    reader.readAsArrayBuffer(file)
    reader.onloadend = () => {
      this.setState({ buffer: Buffer(reader.result) })
      console.log('buffer', this.state.buffer)
    }
  }

  onSubmit = (event) => {
    event.preventDefault()
    console.log("Submitting file to ipfs...")
    const name = event.target.name.value
    const roll = event.target.roll.value
    const key = event.target.key.value
    const useraccount = event.target.account.value
    ipfs.add(this.state.buffer, (error, result) => {
      console.log('Ipfs result', result)
      if(error) {
        console.error(error)
        return
      }
      /*
       this.state.contract.methods.set(result[0].hash).send({ from: this.state.account }).then((r) => {
         return this.setState({ memeHash: result[0].hash }),this.setState({ useraccount: useraccount })
         ,this.setState({ name: name }),this.setState({ roll: roll }),this.setState({ key: key })
         ,this.setState({ hash: result[0].hash })
       })
       */
       this.state.contract.methods.CreateUser(useraccount,key,name,roll,result[0].hash).send({ from: this.state.account }).then((r) => {
        return this.setState({ useraccount: useraccount }),this.setState({ name: name })
        ,this.setState({ roll: roll }),this.setState({ key: key })
        ,this.setState({ hash: result[0].hash })
      })
      console.log('Successfully submitted!!',this.state.useraccount)
    })
    console.log(this.state.useraccount)
  }

  render() {
    return (
      <div>
        <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <a
            className="navbar-brand col-sm-3 col-md-2 mr-0"
            href="App.js"
            target="_blank"
            rel="noopener noreferrer"
          >
            Certificate Authentication
          </a>
          <ul className="navbar-nav px-3">
            <li className="nav-item text-nowrap d-none d-sm-none d-sm-block">
              <p className="text-white">Account Address : </p><small className="text-white">{this.state.account}</small>
            </li>
          </ul>
        </nav>
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">
                <h1 className="mt-5 mr-auto ml-auto" >Admin(Student Registration)</h1>
                <a
                  href=""
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  { //<img src={`https://ipfs.infura.io/ipfs/${this.state.memeHash}`} /> 
                  }
                </a>
                <p>&nbsp;</p>
                <h2 className="mb-5 mr-auto ml-auto">Student form</h2>
                <form className="mb-5" onSubmit={this.onSubmit}>
                  <label>
                    <b>Account Address :</b>
                    <input className="ml-3" type="text" name="account" placeholder="Enter Account Address"/>
                  </label>
                  <p><br></br></p>

                  <label>
                    <b>Private Key :</b>
                    <input className="ml-3" type="text" name="key" placeholder="Enter Private Key"/>
                  </label>
                  <p><br></br></p>

                  <label>
                    <b>Name :</b>
                    <input className="ml-3" type="text" name="name" placeholder="Enter Student Name"/>
                  </label>
                  <p><br></br></p>

                  <label>
                    <b>Roll No :</b>
                    <input className="ml-3" type="text" name="roll" placeholder="Enter Student Roll"/>
                  </label>
                  <p><br></br></p>

                  <input type='file' onChange={this.captureFile} />
                  <p><br></br></p>

                  <input type='submit' style={{backgroundColor: '#00A884'}}/>
                  <p><br></br></p>
                </form>
                <button onClick={this.show}>Show User Info</button>

                <hr style={{
                    color: '#000000',
                    backgroundColor: '#000000',
                    height: .5,
                    borderColor : '#000000'
                }}/>
              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
